// Parse OpenAI response - extract JSON from choices[0].message.content
const openAiOutput = $input.item.json;
let textResult = openAiOutput;

// Extract content from OpenAI response structure
if (openAiOutput.choices && openAiOutput.choices[0] && openAiOutput.choices[0].message) {
  textResult = openAiOutput.choices[0].message.content;
}

// Parse JSON if it's a string
let parsedText = textResult;
if (typeof textResult === 'string') {
  try {
    parsedText = JSON.parse(textResult);
  } catch (e) {
    // If parsing fails, try to extract JSON from text
    const jsonMatch = textResult.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      parsedText = JSON.parse(jsonMatch[0]);
    } else {
      throw new Error('Could not parse text transformation response as JSON');
    }
  }
}

// Validate structure
if (!parsedText.horoscope || !Array.isArray(parsedText.dos) || !Array.isArray(parsedText.donts)) {
  throw new Error('Invalid response format from OpenAI text transformation');
}

// Store text result and pass through webhook data
// Get webhook data from input (passed through from Create Horoscope node)
// The input should have the original webhook data
const inputData = $input.item.json;

// Ensure slots and reasoning are preserved as objects (not strings or flattened)
// n8n webhook may flatten nested objects or convert them to strings, so we need to handle both
let slots = inputData.slots || null;
let reasoning = inputData.reasoning || null;

// If slots/reasoning are strings, parse them as JSON
if (slots && typeof slots === 'string') {
  try {
    slots = JSON.parse(slots);
  } catch (e) {
    console.error('Failed to parse slots as JSON:', e);
    slots = null;
  }
}

if (reasoning && typeof reasoning === 'string') {
  try {
    reasoning = JSON.parse(reasoning);
  } catch (e) {
    console.error('Failed to parse reasoning as JSON:', e);
    reasoning = null;
  }
}

// Ensure they are objects (not arrays or other types)
if (slots && typeof slots !== 'object' || Array.isArray(slots)) {
  slots = null;
}
if (reasoning && typeof reasoning !== 'object' || Array.isArray(reasoning)) {
  reasoning = null;
}

// If slots/reasoning are not objects, try to reconstruct from flattened keys
if (!slots && inputData) {
  // Check if slots properties are at the top level (flattened)
  if (inputData.style_medium_id || inputData.style_reference_id) {
    slots = {
      style_medium_id: inputData.style_medium_id,
      style_reference_id: inputData.style_reference_id,
      subject_role_id: inputData.subject_role_id,
      subject_twist_id: inputData.subject_twist_id,
      setting_place_id: inputData.setting_place_id,
      setting_time_id: inputData.setting_time_id,
      activity_id: inputData.activity_id,
      mood_vibe_id: inputData.mood_vibe_id,
      color_palette_id: inputData.color_palette_id,
      camera_frame_id: inputData.camera_frame_id,
      lighting_style_id: inputData.lighting_style_id,
      constraints_ids: inputData.constraints_ids || (inputData['constraints_ids[0]'] ? [
        inputData['constraints_ids[0]'],
        inputData['constraints_ids[1]'],
        inputData['constraints_ids[2]'],
        inputData['constraints_ids[3]']
      ].filter(Boolean) : [])
    };
  }
}

if (!reasoning && inputData) {
  // Check if reasoning properties are at the top level (flattened)
  if (inputData['reasoning.style_reference'] || inputData.style_reference) {
    reasoning = {
      style_reference: inputData['reasoning.style_reference'] || inputData.style_reference,
      style_medium: inputData['reasoning.style_medium'] || inputData.style_medium,
      subject_role: inputData['reasoning.subject_role'] || inputData.subject_role,
      subject_twist: inputData['reasoning.subject_twist'] || inputData.subject_twist,
      setting_place: inputData['reasoning.setting_place'] || inputData.setting_place,
      setting_time: inputData['reasoning.setting_time'] || inputData.setting_time,
      activity: inputData['reasoning.activity'] || inputData.activity,
      mood_vibe: inputData['reasoning.mood_vibe'] || inputData.mood_vibe,
      color_palette: inputData['reasoning.color_palette'] || inputData.color_palette,
      camera_frame: inputData['reasoning.camera_frame'] || inputData.camera_frame,
      lighting_style: inputData['reasoning.lighting_style'] || inputData.lighting_style,
      constraints: inputData['reasoning.constraints'] || inputData.constraints
    };
  }
}

return {
  json: {
    horoscope: parsedText.horoscope,
    dos: parsedText.dos,
    donts: parsedText.donts,
    imagePrompt: inputData.imagePrompt || null,
    slots: slots,
    reasoning: reasoning
  }
};

// Extract image URL from OpenAI response before n8n converts it to a file object
// This node runs immediately after OpenAI Image Generation (Create Avatar) to capture the URL
const openAiResponse = $input.item.json;

console.log('OpenAI Image Generation response keys:', Object.keys(openAiResponse || {}));
console.log('Full response:', JSON.stringify(openAiResponse, null, 2));

let imageUrl = null;

// Check for Create Avatar output format: { url, revised_prompt }
if (openAiResponse.url && openAiResponse.revised_prompt) {
  imageUrl = openAiResponse.url;
  console.log('✅ Found image URL from Create Avatar node:', imageUrl);
}
// OpenAI API returns: { data: [{ url: '...' }] }
else if (openAiResponse.data && Array.isArray(openAiResponse.data) && openAiResponse.data[0] && openAiResponse.data[0].url) {
  imageUrl = openAiResponse.data[0].url;
  console.log('✅ Found image URL in data array:', imageUrl);
}
// Check if URL is in the json directly
else if (openAiResponse.url) {
  imageUrl = openAiResponse.url;
  console.log('✅ Found image URL in json.url:', imageUrl);
}
// Check if there's a response property with the original API response
else if (openAiResponse.response && openAiResponse.response.data && Array.isArray(openAiResponse.response.data) && openAiResponse.response.data[0] && openAiResponse.response.data[0].url) {
  imageUrl = openAiResponse.response.data[0].url;
  console.log('✅ Found image URL in response.data:', imageUrl);
}
else {
  console.error('❌ Could not find URL in OpenAI response');
  console.error('Response structure:', JSON.stringify(openAiResponse, null, 2));
  throw new Error('Failed to extract image URL from OpenAI Image Generation response. Response structure: ' + JSON.stringify(Object.keys(openAiResponse || {})));
}

// Get original webhook data from input
// Note: OpenAI nodes may not pass through all input data, so we try to get what we can
const inputData = $input.item.json;

// Ensure slots and reasoning are preserved as objects (not strings)
// If they're not in inputData (OpenAI node might not pass them through), they'll be null
// and we'll get them from the merged text result in Combine Results
let slots = inputData.slots || null;
let reasoning = inputData.reasoning || null;

// If slots/reasoning are strings, parse them as JSON
if (slots && typeof slots === 'string') {
  try {
    slots = JSON.parse(slots);
  } catch (e) {
    console.error('Failed to parse slots as JSON:', e);
    slots = null;
  }
}

if (reasoning && typeof reasoning === 'string') {
  try {
    reasoning = JSON.parse(reasoning);
  } catch (e) {
    console.error('Failed to parse reasoning as JSON:', e);
    reasoning = null;
  }
}

// Ensure they are objects (not arrays or other types)
if (slots && (typeof slots !== 'object' || Array.isArray(slots))) {
  slots = null;
}
if (reasoning && (typeof reasoning !== 'object' || Array.isArray(reasoning))) {
  reasoning = null;
}

// If slots/reasoning are not objects, try to reconstruct from flattened keys (same logic as Parse Text Result)
if (!slots && inputData) {
  if (inputData.style_medium_id || inputData.style_reference_id) {
    slots = {
      style_medium_id: inputData.style_medium_id,
      style_reference_id: inputData.style_reference_id,
      subject_role_id: inputData.subject_role_id,
      subject_twist_id: inputData.subject_twist_id,
      setting_place_id: inputData.setting_place_id,
      setting_time_id: inputData.setting_time_id,
      activity_id: inputData.activity_id,
      mood_vibe_id: inputData.mood_vibe_id,
      color_palette_id: inputData.color_palette_id,
      camera_frame_id: inputData.camera_frame_id,
      lighting_style_id: inputData.lighting_style_id,
      constraints_ids: inputData.constraints_ids || (inputData['constraints_ids[0]'] ? [
        inputData['constraints_ids[0]'],
        inputData['constraints_ids[1]'],
        inputData['constraints_ids[2]'],
        inputData['constraints_ids[3]']
      ].filter(Boolean) : [])
    };
  }
}

if (!reasoning && inputData) {
  if (inputData['reasoning.style_reference'] || inputData.style_reference) {
    reasoning = {
      style_reference: inputData['reasoning.style_reference'] || inputData.style_reference,
      style_medium: inputData['reasoning.style_medium'] || inputData.style_medium,
      subject_role: inputData['reasoning.subject_role'] || inputData.subject_role,
      subject_twist: inputData['reasoning.subject_twist'] || inputData.subject_twist,
      setting_place: inputData['reasoning.setting_place'] || inputData.setting_place,
      setting_time: inputData['reasoning.setting_time'] || inputData.setting_time,
      activity: inputData['reasoning.activity'] || inputData.activity,
      mood_vibe: inputData['reasoning.mood_vibe'] || inputData.mood_vibe,
      color_palette: inputData['reasoning.color_palette'] || inputData.color_palette,
      camera_frame: inputData['reasoning.camera_frame'] || inputData.camera_frame,
      lighting_style: inputData['reasoning.lighting_style'] || inputData.lighting_style,
      constraints: inputData['reasoning.constraints'] || inputData.constraints
    };
  }
}

// Return the image URL along with webhook data
return {
  json: {
    imageUrl: imageUrl,
    imagePrompt: inputData.imagePrompt || openAiResponse.revised_prompt || null,
    slots: slots,
    reasoning: reasoning
  }
};

// This node receives merged data from both Parse Text Result and OpenAI Image Generation
// The Merge node combines them, so we get both in $input.all()

// Get all input items from the merge (should be 2: one from text path, one from image path)
const inputItems = $input.all();

console.log('Number of input items:', inputItems.length);
console.log('Input items:', inputItems.map(item => ({ keys: Object.keys(item.json || {}), hasBinary: !!item.binary })));

// Find text result (from Parse Text Result node)
let textResult = null;
let imageOutput = null;
let imageItem = null;

for (const item of inputItems) {
  const data = item.json;
  // Text result has horoscope, dos, donts properties
  if (data.horoscope && data.dos && data.donts) {
    textResult = data;
    console.log('✅ Found text result');
  }
  // Image output - check for imageUrl from Extract Image URL node
  else if (data.imageUrl) {
    imageOutput = data;
    imageItem = item;
    console.log('✅ Found image output (from Extract Image URL node)');
  }
  // Image output - check for url and revised_prompt (from Create Avatar/OpenAI Image Generation)
  else if (data.url && data.revised_prompt) {
    imageOutput = data;
    imageItem = item;
    console.log('✅ Found image output (url + revised_prompt)');
  }
  // Image output - check for file object properties
  else if (data.mimeType || data.fileType || data.fileName || data.fileSize) {
    imageOutput = data;
    imageItem = item;
    console.log('✅ Found image output (file object)');
  }
  // Image output - check for OpenAI API response format
  else if (data.data && Array.isArray(data.data) && data.data[0] && (data.data[0].url || data.data[0].b64_json)) {
    imageOutput = data;
    imageItem = item;
    console.log('✅ Found image output (API response)');
  }
  // Image output - check for direct url property (without horoscope)
  else if (data.url && !data.horoscope && !data.dos && !data.donts) {
    imageOutput = data;
    imageItem = item;
    console.log('✅ Found image output (direct url)');
  }
  // If it's not text result and not already identified, it might be the image
  else if (!data.horoscope && !data.dos && !data.donts && !data.imagePrompt) {
    imageOutput = data;
    imageItem = item;
    console.log('✅ Found image output (fallback - not text result)');
  }
}

if (!textResult) {
  console.error('❌ Text result not found. Available items:', inputItems.map(item => Object.keys(item.json || {})));
  throw new Error('Failed to get text result. Make sure Parse Text Result node completed successfully.');
}

if (!imageOutput) {
  console.error('❌ Image output not found. Available items:', inputItems.map(item => ({ keys: Object.keys(item.json || {}), hasBinary: !!item.binary })));
  throw new Error('Failed to get image output. Make sure OpenAI Image Generation node completed successfully. Check the execution logs to see what data was received.');
}

// Extract image URL from the merged data
// The Extract Image URL node should have already extracted it, so check for imageUrl first
let imageUrl = null;

// First check if imageUrl was already extracted by Extract Image URL node
if (imageOutput.imageUrl) {
  imageUrl = imageOutput.imageUrl;
  console.log('✅ Found image URL from Extract Image URL node');
}
// Check for url property (from Create Avatar/OpenAI Image Generation with revised_prompt)
else if (imageOutput.url) {
  imageUrl = imageOutput.url;
  console.log('✅ Found image URL in url property:', imageUrl);
}
// Standard OpenAI API response format: { data: [{ url: '...' }] }
else if (imageOutput.data && Array.isArray(imageOutput.data) && imageOutput.data[0] && imageOutput.data[0].url) {
  imageUrl = imageOutput.data[0].url;
  console.log('✅ Found image URL in data array');
}
// If it's a file object (has mimeType, fileType, etc.), check binary data for URL
else if (imageOutput.mimeType || imageOutput.fileType) {
  console.error('❌ Image was downloaded as file object. Checking binary data for URL...');

  // Check if we can get the URL from the binary data metadata
  if (imageItem && imageItem.binary) {
    const binaryKeys = Object.keys(imageItem.binary);
    if (binaryKeys.length > 0) {
      const binaryData = imageItem.binary[binaryKeys[0]];
      // Check various possible locations for URL in binary metadata
      if (binaryData && binaryData.meta && binaryData.meta.url) {
        imageUrl = binaryData.meta.url;
        console.log('✅ Found image URL in binary metadata');
      } else if (binaryData && binaryData.data && binaryData.data.url) {
        imageUrl = binaryData.data.url;
        console.log('✅ Found image URL in binary data');
      }
    }
  }
}

if (!imageUrl) {
  // Log the actual structure for debugging
  console.error('Failed to extract image URL. Response structure:', Object.keys(imageOutput));
  console.error('Full response:', JSON.stringify(imageOutput, null, 2));
  // Also check the full item structure
  const imageItem = inputItems.find(item => item.json.mimeType || item.json.fileType || (!item.json.horoscope && !item.json.dos));
  if (imageItem) {
    console.error('Full image item:', JSON.stringify(imageItem, null, 2));
  }
  throw new Error('Failed to extract image URL from OpenAI response. The image was downloaded as a file object without URL metadata. Please ensure Return URL is enabled in the OpenAI Image Generation node settings, or add a Code node after the OpenAI Image Generation node to extract the URL before it gets converted to a file.');
}

// Get original webhook data (imagePrompt, slots, reasoning) from the merged items
// Try to get it from text result first (should have it from Parse Text Result)
// Then try from image item (from Extract Image URL node)
const webhookData = {
  imagePrompt: textResult?.imagePrompt || imageItem?.json?.imagePrompt || imageItem?.json?.prompt || null,
  slots: textResult?.slots || imageItem?.json?.slots || null,
  reasoning: textResult?.reasoning || imageItem?.json?.reasoning || null
};

console.log('Webhook data retrieved:', {
  hasImagePrompt: !!webhookData.imagePrompt,
  hasSlots: !!webhookData.slots,
  hasReasoning: !!webhookData.reasoning,
  slotsKeys: webhookData.slots ? Object.keys(webhookData.slots) : null
});

// Combine all results
return {
  json: {
    horoscope: textResult.horoscope,
    dos: textResult.dos,
    donts: textResult.donts,
    imageUrl: imageUrl,
    prompt: webhookData.imagePrompt,
    slots: webhookData.slots,
    reasoning: webhookData.reasoning
  }
};
