// Parse OpenAI response - extract JSON from choices[0].message.content
const openAiOutput = $input.item.json;
let textResult = openAiOutput;

// Extract content from OpenAI response structure
if (openAiOutput.choices && openAiOutput.choices[0] && openAiOutput.choices[0].message) {
  textResult = openAiOutput.choices[0].message.content;
}

// Parse JSON if it's a string
let parsedText = textResult;
if (typeof textResult === 'string') {
  try {
    parsedText = JSON.parse(textResult);
  } catch (e) {
    // If parsing fails, try to extract JSON from text
    const jsonMatch = textResult.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      parsedText = JSON.parse(jsonMatch[0]);
    } else {
      throw new Error('Could not parse text transformation response as JSON');
    }
  }
}

// Validate structure
if (!parsedText.horoscope || !Array.isArray(parsedText.dos) || !Array.isArray(parsedText.donts)) {
  throw new Error('Invalid response format from OpenAI text transformation');
}

// Store text result and pass through original webhook data for image generation
return {
  json: {
    horoscope: parsedText.horoscope,
    dos: parsedText.dos,
    donts: parsedText.donts,
    imagePrompt: $('Webhook').item.json.imagePrompt,
    slots: $('Webhook').item.json.slots,
    reasoning: $('Webhook').item.json.reasoning
  }
};

// This node receives merged data from both Parse Text Result and OpenAI Image Generation
// The Merge node combines them, so we get both in $input.all()

// Get all input items from the merge (should be 2: one from text path, one from image path)
const inputItems = $input.all();

// Find text result (from Parse Text Result node)
let textResult = null;
let imageOutput = null;

for (const item of inputItems) {
  const data = item.json;
  // Text result has horoscope, dos, donts properties
  if (data.horoscope && data.dos && data.donts) {
    textResult = data;
  }
  // Image output has data array or url property (from OpenAI Image Generation)
  else if (data.data || data.url || data.response || (data.choices === undefined && !data.horoscope)) {
    imageOutput = data;
  }
}

// Fallback: try getting directly from nodes if merge didn't work as expected
if (!textResult) {
  try {
    textResult = $('Parse Text Result').item.json;
  } catch (e) {
    console.error('Could not get text result from Parse Text Result node');
  }
}

if (!imageOutput) {
  try {
    const imageGenItem = $('OpenAI Image Generation').item;
    imageOutput = imageGenItem.json;
    // Also check if there's binary data with metadata
    if (imageGenItem.binary) {
      const binaryKeys = Object.keys(imageGenItem.binary);
      if (binaryKeys.length > 0) {
        const binaryData = imageGenItem.binary[binaryKeys[0]];
        // Check if URL is stored in binary metadata
        if (binaryData && binaryData.meta && binaryData.meta.url) {
          imageOutput = { ...imageOutput, url: binaryData.meta.url };
        }
      }
    }
  } catch (e) {
    console.error('Could not get image output from OpenAI Image Generation node');
  }
}

if (!textResult) {
  throw new Error('Failed to get text result. Make sure Parse Text Result node completed successfully.');
}

if (!imageOutput) {
  throw new Error('Failed to get image output. Make sure OpenAI Image Generation node completed successfully.');
}

// Extract image URL from OpenAI response
// With

 enabled, OpenAI should return: { data: [{ url: '...' }] }
let imageUrl = null;

// Standard OpenAI API response format: { data: [{ url: '...' }] }
if (imageOutput.data && Array.isArray(imageOutput.data) && imageOutput.data[0] && imageOutput.data[0].url) {
  imageUrl = imageOutput.data[0].url;
}
// Fallback: direct url property
else if (imageOutput.url) {
  imageUrl = imageOutput.url;
}
// Fallback: try getting from the node directly
else {
  try {
    const imageGenItem = $('OpenAI Image Generation').item;
    if (imageGenItem.json && imageGenItem.json.data && Array.isArray(imageGenItem.json.data) && imageGenItem.json.data[0] && imageGenItem.json.data[0].url) {
      imageUrl = imageGenItem.json.data[0].url;
    } else if (imageGenItem.json && imageGenItem.json.url) {
      imageUrl = imageGenItem.json.url;
    }
  } catch (e) {
    console.error('Could not access OpenAI Image Generation node directly:', e);
  }
}

if (!imageUrl) {
  // Log the actual structure for debugging
  console.error('Failed to extract image URL. Response structure:', Object.keys(imageOutput));
  console.error('Full response:', JSON.stringify(imageOutput, null, 2));
  // Also check the full item structure
  const imageItem = inputItems.find(item => item.json.mimeType || item.json.fileType || (!item.json.horoscope && !item.json.dos));
  if (imageItem) {
    console.error('Full image item:', JSON.stringify(imageItem, null, 2));
  }
  throw new Error('Failed to extract image URL from OpenAI response. Response structure: ' + JSON.stringify(Object.keys(imageOutput)));
}

// Get original webhook data for slots and reasoning
const webhookData = $('Webhook').item.json;

// Combine all results
return {
  json: {
    horoscope: textResult.horoscope,
    dos: textResult.dos,
    donts: textResult.donts,
    imageUrl: imageUrl,
    prompt: webhookData.imagePrompt || textResult.imagePrompt,
    slots: webhookData.slots || textResult.slots,
    reasoning: webhookData.reasoning || textResult.reasoning
  }
};
